select T.LEVEL xray, T1.LEVEL proton, T2.LEVEL ele, T3.LEVEL dst, bef24h, aft3day
from (select LEVEL
      from SEC_XRAY_ALARM
      where THRESHOLD_TIME >= #{startTime}
        AND THRESHOLD_TIME <= #{endTime}
     ) T,
     (select LEVEL
      from SEC_PROTON_ALARM
      where THRESHOLD_TIME >= #{startTime}
        AND THRESHOLD_TIME <= #{endTime}
     ) T1,
     (select LEVEL
      from SEC_ELE_ALARM
      where THRESHOLD_TIME >= #{startTime}
        AND THRESHOLD_TIME <= #{endTime}
     ) T2,
     (select LEVEL
      from SEC_DST_ALARM
      where THRESHOLD_TIME >= #{startTime}
        AND THRESHOLD_TIME <= #{endTime}
     ) T3,
     (select PAST_REVIEW bef24h, FUTURE_REVIEW aft3day
      FROM SEC_DAILY_OVERVIEW
      where TIME >= #{startTime}
        AND TIME <= #{endTime}) T4

choose.when:
UPDATE SEC_WEEK_OVERVIEW
SET path=#{path}
WHERE TIME=#{time};
COMMIT;

UPDATE SEC_MONTH_OVERVIEW
SET path=#{path}
WHERE TIME=#{time};
COMMIT;

choose.otherwise:
UPDATE SEC_DAILY_OVERVIEW
SET path=#{path}
WHERE TIME=#{time};
COMMIT;
